import { useState } from "react";
import { InputData } from "../components/create/InputData";
import { Theme } from "../others/Theme";
import axios from "axios";
import { CreateButton } from "../components/create/CreateButton";
import { SwitchButton } from "../components/login/SwitchButton";

export const Login = () => {
  const [user, setUser] = useState(userDefaultState);
  useEffect(() => {
    axios
      .get("http://localhost:3001/api/items")
      .then((res) => {
        console.log(res);
        setItems(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [setItems]);
  return (
    <Box
      sx={{
        bgcolor: Theme.palette.secondary.light,
        boxShadow: 1,
        borderRadius: 5,
        p: 2,
        m: 3,
        "&:hover": {
          backgroundColor: Theme.palette.secondary.main,
        },
      }}
    >
      <InputData
        value={user.userName}
        onChange={(e) =>
          setUser({ ...user, userName: e.target.value, id: e.target.value })
        }
        label={"userName"}
      />
      <InputData
        value={user.email}
        onChange={(e) => setUser({ ...user, email: e.target.value })}
        label={"email"}
      />
      <InputData
        value={user.password}
        onChange={(e) => setUser({ ...user, password: e.target.value })}
        label={"password"}
      />
      <CreateButton addUser={addUser} text={'register'} />
      <SwitchButton text={'Login'} href={"/login"}/>
    </Box>
  );
};
