import { Box } from "@mui/system";
import { useState, useEffect } from "react";
import { InputData } from "../components/create/InputData";
import { Theme } from "../others/Theme";
import axios from "axios";
import { CreateButton } from "../components/create/CreateButton";
import { SwitchButton } from "../components/login/SwitchButton";
import md5 from "md5";
import { useNavigate } from "react-router-dom";


const userDefaultState = {
 nameOrEmail: "",
  password: "",
};

export const Login = () => {
  const [user, setUser] = useState(userDefaultState);
  const [errors, setErrors] = useState(null);
  let navigate = useNavigate();

  const addUserLogin = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:3001/api/auth", user)
      .then(function (response) {
        console.log(response);
        localStorage.setItem("token", response.data)
        navigate("/login")
      })
      .catch(function (error) {
        console.log(error);
        setErrors(error.response.data);
      });

    setUser(userDefaultState);
  };
  // useEffect(() => {
  //   setUser({ ...user, id: md5(user.nameOrEmail)})
  // }, [user.nameOrEmail])
  return (
    <Box
      sx={{
        bgcolor: Theme.palette.secondary.light,
        boxShadow: 1,
        borderRadius: 5,
        p: 2,
        m: 3
      }}
    >
      <InputData
        value={user.email}
        onChange={(e) =>
          setUser({ ...user, nameOrEmail: e.target.value})
        }
        label={"nameOrEmail"}
      />
      <InputData
        value={user.password}
        onChange={(e) => setUser({ ...user, password: e.target.value })}
        label={"password"}
      />
      <CreateButton addUserLogin={addUserLogin} text={'login'} />
      <SwitchButton text={'Register'} href={"/register"}/>
    </Box>
  );
};
