import { Box, Button, Grid, Typography } from "@mui/material";
import axios from "axios";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { AddToCartItem } from "../components/home/AddToCartItem";
import file1 from "../others/file1.png";
export const ItemPage = () => {
  const params = useParams();
  const [item, setItem] = useState();
  useEffect(() => {
    axios
      .get(`http://localhost:3001/api/items/${params.id}`)
      .then((res) => {
        console.log(res);
        setItem(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [params.id]);
  if (item?.name)
    return (
      <>
        <Box>
          <img src={file1} />
        </Box>
        <Box>
          <Typography align="Center" variant="h5">
            {item.name}
          </Typography>
          <Typography variant="h6">description:{item.description}</Typography>
          <Typography variant="h6">tag:{item.tag}</Typography>
          <Typography variant="h6">quantity:{item.quantity}</Typography>
          <Typography variant="h6">blockchain:{item.blockchain}</Typography>
        </Box>
        <Box><AddToCartItem item={item} params={params} />
        </Box>
      </>
    );
};
