import { Box } from "@mui/system";
import { useState, useEffect } from "react";
import { InputData } from "../components/create/InputData";
import { Theme } from "../others/Theme";
import axios from "axios";
import { CreateButton } from "../components/create/CreateButton";
import { SwitchButton } from "../components/login/SwitchButton";

const userDefaultState = {
  userName: "",
  email: "",
  password: "",
};

export const Login = () => {
  const [checkingUser, setCheckingUser] = useState();
  const addCheckingUser = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:3001/api/login", checkingUser)
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

      setCheckingUser(userDefaultState);
  };
  return (
    <Box
      sx={{
        bgcolor: Theme.palette.secondary.light,
        boxShadow: 1,
        borderRadius: 5,
        p: 2,
        m: 3,
        "&:hover": {
          backgroundColor: Theme.palette.secondary.main,
        },
      }}
    >
      <InputData
        value={checkingUser.userName}
        onChange={(e) =>
          setCheckingUser({ ...checkingUser, userName: e.target.value})
        }
        label={"userName"}
      />
      <InputData
        value={checkingUser.email}
        onChange={(e) => setCheckingUser({ ...checkingUser, email: e.target.value })}
        label={"email"}
      />
      <InputData
        value={checkingUser.password}
        onChange={(e) => setCheckingUser({ ...checkingUser, password: e.target.value })}
        label={"password"}
      />
      <CreateButton addUser={addUser} text={'register'} />
      <SwitchButton text={'Login'} href={"/login"}/>
    </Box>
  );
};
