import { Box } from "@mui/system";
import { useState, useEffect } from "react";
import { InputData } from "../components/create/InputData";
import { Theme } from "../others/Theme";
import axios from "axios";
// import { CreateButton } from "../components/create/CreateButton";
import { SwitchButton } from "../components/login/SwitchButton";

export const Login = () => {
  const [user, setUser] = useState();
  const [checkingUser, setCheckingUser] = useState();
  useEffect(() => {
    axios
      .get("http://localhost:3001/api/users")
      .then((res) => {
        console.log(res);
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [setUser]);
  useEffect
  return (
    <Box
      sx={{
        bgcolor: Theme.palette.secondary.light,
        boxShadow: 1,
        borderRadius: 5,
        p: 2,
        m: 3,
        "&:hover": {
          backgroundColor: Theme.palette.secondary.main,
        },
      }}
    >
      <InputData
        value={checkingUser.userName}
        onChange={(e) =>
          setUser({ ...checkingUser, userName: e.target.value, id: e.target.value })
        }
        label={"userName"}
      />
      <InputData
        value={checkingUser.email}
        onChange={(e) => setUser({ ...checkingUser, email: e.target.value })}
        label={"email"}
      />
      <InputData
        value={checkingUser.password}
        onChange={(e) => setUser({ ...checkingUser, password: e.target.value })}
        label={"password"}
      />
      {/* <CreateButton addUser={addUser} text={'register'} /> */}
      <SwitchButton text={'Login'} href={"/login"}/>
    </Box>
  );
};
