import * as React from "react";
import { Autocomplete, Stack, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useState, Component } from "react";
import Dropzone from "react-dropzone";

export const CreateProduct = () => {


  const [itemName, setItemName] = useState()
  const [itemFile, setItemFile] = useState()
  const [itemBlockchain, setitemBlockchain] = useState()
  const [itemTag, setitemTag] = useState()
  const [itemExternalLink, setitemExternalLink] = useState()
  const [itemQuantity, setitemQuantity] = useState()

  const handleImage = (e) => {
    console.log(e.target.files);
    setItemFile(URL.createObjectURL(e.target.files[0]));
  };

  console.log(image);
  const handleChange = (event) => {
    setValue(event.target.value);
  };
  const handleDrop = () => {
    console.log();
  };

  const addItemName = (event) => {
    event.preventDefault()
    const ItemObject = {
      name: itemName,
      id: Math.random(),
      file: acceptedFiles,
      blockchain: itemBlockchain,
      tag: itemTag,
      externalLink: externalLink,
      quantity: quantity,
    }




  return (
    <Box>
      <Box>
        <Dropzone onDrop={(acceptedFiles) => console.log(acceptedFiles)}>
          {({ getRootProps, getInputProps }) => (
            <section>
              <div {...getRootProps()}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
              </div>
            </section>
          )}
        </Dropzone>
      </Box>
      <Box
        sx={{
          width: 300,
        }}
      >
        <Typography variant="h6">choose a category</Typography>
        <Autocomplete
          freeSolo
          id="free-solo-2-demo"
          disableClearable
          options={tagArr.map((option) => option.title)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="choose a category"
              InputProps={{
                ...params.InputProps,
                type: "search",
              }}
            />
          )}
        />
      </Box>
      <Box
        sx={{
          width: 300,
        }}
      >
        <Typography variant="h6">choose a blockchain</Typography>
        <Autocomplete
          freeSolo
          id="free-solo-2-demo"
          disableClearable
          options={blockchainArr.map((option) => option.title)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="choose a category"
              InputProps={{
                ...params.InputProps,
                type: "search",
              }}
            />
          )}
        />
      </Box>

      <Box
        component="form"
        sx={{
          "& .MuiTextField-root": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        <div>
          <TextField
            id="outlined-multiline-flexible"
            label="Name"
            multiline
            maxRows={4}
            value={value}
            onChange={handleChange}
          />
          <TextField
            id="outlined-textarea"
            label="Quantity"
            placeholder="Placeholder"
            multiline
          />
          <TextField
            id="outlined-multiline-static"
            label="External link"
            multiline
            rows={4}
            defaultValue="33333333"
          />
        </div>
        <div>


          <TextField
            id="filled-multiline-static"
            label="Description"
            multiline
            rows={4}
            defaultValue="Default Value"
            variant="filled"
          />
        </div>
        <div>



        </div>
      </Box>
    </Box>
  );
};

const tagArr = [
  { title: "Art" },
  { title: "Music" },
  { title: "Photography" },
  { title: "Collectibles" },
  { title: "Domain" },
];
const blockchainArr = [
  { title: "blockchain1" },
  { title: "blockchain2" },
  { title: "blockchain3" },
  { title: "blockchain4" },
  { title: "blockchain5" },
];}
