import { Box } from "@mui/system";
import { useState, useEffect } from "react";
import { InputData } from "../components/create/InputData";
import { Theme } from "../others/Theme";
import axios from "axios";
import { CreateButton } from "../components/create/CreateButton";
import { SwitchButton } from "../components/login/SwitchButton";
import md5 from "md5";

const userDefaultState = {
 userName: "",
  email: "",
  password: "",
};

export const Register = () => {
  const [user, setUser] = useState(userDefaultState);
  const [formError, setFormError] = useState();
  const validate = (user) => {
    console.log(user, 'it is user')
    const error = {}
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if(!user.userName){
      error.userName = "Username is required!"
    }
    if(!user.email){
      error.userName = "Email is required!"
    }
    if(!user.password){
      error.userName = "Password is required!"
    }
  }

  const addUser = (event) => {
    event.preventDefault();
    setFormError(validate(user))
    axios
      .post("http://localhost:3001/api/users", user)
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });

    setUser(userDefaultState);
  };
  useEffect(() => {
    setUser({ ...user, id: md5(user.email)})
  }, [user.email])
  return (
    <Box
      sx={{
        bgcolor: Theme.palette.secondary.light,
        boxShadow: 1,
        borderRadius: 5,
        p: 2,
        m: 3
      }}
    >
      <InputData
        value={user.userName}
        onChange={(e) =>
          setUser({ ...user, userName: e.target.value})
        }
        label={"userName"}
      />
      <InputData
        value={user.email}
        onChange={(e) => setUser({ ...user, email: e.target.value })}
        label={"email"}
      />
      <InputData
        value={user.password}
        onChange={(e) => setUser({ ...user, password: e.target.value })}
        label={"password"}
      />
      <CreateButton addUser={addUser} text={'register'} />
      <SwitchButton text={'Login'} href={"/login"}/>
    </Box>
  );
};
