import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Collapse,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import { Grid } from "@mui/material";
import { ExpandMore } from "@material-ui/icons";
import { useState } from "react";
import file1 from "../../others/file1.png";
import axios from "axios";

export const Items = (item) => {
  const [expanded, setExpanded] = useState(false);

  const deleteFromCart = () => {
    axios
      .delete(`http://localhost:3001/api/cart/${item.id}`)
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.error("There was an error!", error);
      });
  };
  const x = useCallback(() => {
    localStorage.removeItem("token");
    window.location = "/login";
  }, []);
  return (
    <Grid xs display="flex" justifyContent="center" alignItems="center">
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          p: 1,
          m: 1,
          bgcolor: "background.paper",
          maxWidth: 600,
          borderRadius: 1,
        }}
      >
        <Card style={{ maxWidth: 200 }}>
          <CardContent>
            <Typography variant="h5">{item.name}</Typography>
          </CardContent>
          <CardMedia image={file1} component="img" />
          <ExpandMore onClick={() => setExpanded(!expanded)} />
          <Collapse in={expanded}>
            <CardContent>
              <Typography variant="h6">
                description:{item.description}
              </Typography>
              <Typography variant="h6">tag:{item.tag}</Typography>
              <Typography variant="h6">quantity:{item.quantity}</Typography>
              <Typography variant="h6">blockchain:{item.blockchain}</Typography>
              <Button
                variant="contained"
                target="_blank"
                href={`/itempage/${item.id}`}
              >
                see more
              </Button>
              <Button
                variant="contained"
                target="_blank"
                onClick={deleteFromCart}
              >
                delete
              </Button>
            </CardContent>
          </Collapse>
        </Card>
      </Box>
    </Grid>
  );
};
