import * as React from "react";
import { Box, Stack, Grid } from "@mui/material";
import { useState, useEffect } from "react";
import createServece from "../services/items";
import { InputData } from "../components/create/InputData";
import { DragZone } from "../components/create/DragZone";
import { CreateButton } from "../components/create/CreateButton";
import { SelectBlock } from "../components/create/SelectBlock";

const nftDefaultState = {
  name: "",
  file: "",
  description: "",
  tag: "",
  externalLink: "",
  quantity: "",
  blockchain: "",
};

export const CreateProduct = () => {
  const [items, setItems] = useState();
  const [nft, setNft] = useState(nftDefaultState);
  useEffect(() => {
    createServece.getAll().then((initialItems) => {
      setItems(initialItems);
    });
  }, []);

  const addNft = (event) => {
    event.preventDefault();
    setItems(addNft)
    // createServece.create(nft).then((returnedItem) => {
    //   setItems(items.concat(returnedItem));
    // });

    setNft(nftDefaultState);
    console.log(items);
  };
  return (
    <>
      <DragZone onDrop={(file) => setNft({ ...nft, file })} />
      <Box sx={{ width: "100%", ml: 2 }}>
        <Stack direction="row" spacing={2}>
          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            <InputData
              value={nft.name}
              onChange={(e) => setNft({ ...nft, name: e.target.value })}
              label={"Name"}
            />
            <InputData
              value={nft.externalLink}
              onChange={(e) => setNft({ ...nft, externalLink: e.target.value })}
              label={"externalLink"}
            />

            <InputData
              value={nft.quantity}
              onChange={(e) => setNft({ ...nft, quantity: e.target.value })}
              label={"Quantity"}
            />
          </Grid>

          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            <SelectBlock
              value={nft.tag}
              onChange={(e) => setNft({ ...nft, tag: e.target.value })}
              label={"Tag"}
            />
            <SelectBlock
              value={nft.blockchain}
              onChange={(e) => setNft({ ...nft, blockchain: e.target.value })}
              label={"Blockchain"}
            />
          </Grid>
        </Stack>
      </Box>
      <InputData
        value={nft.description}
        onChange={(e) => setNft({ ...nft, description: e.target.value })}
        label={"Description"}
      />
      <CreateButton addNft={addNft} />
    </>
  );
};
