import * as React from "react";
import {
  Box,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  TextField,
  Typography,
  Button,
  Stack,
  Grid,
} from "@mui/material";
import { useState, useEffect } from "react";
import { Theme } from "../others/Theme";
import createServece from "../services/items";
import { InputData } from "../components/InputData";
import { DragZone } from "../components/DragZone";
import { CreateButton } from "../components/CreateButton";
import { SelectBlock } from "../components/CreateButton";

export const CreateProduct = () => {
  const [items, setItems] = useState();
  const [nft, setNft] = useState({
    name: "",
    file: "",
    description: "",
    tag: "",
    externalLink: "",
    quantity: "",
    blockchain: "",
  });
  useEffect(() => {
    createServece.getAll().then((initialItems) => {
      setItems(initialItems);
    });
  }, []);

  const addNft = (event) => {
    event.preventDefault();

    // createServece.create(nft).then((returnedItem) => {
    //   setItems(items.concat(returnedItem));
    // });

    setNft({
      name: "",
      file: "",
      description: "",
      tag: "",
      externalLink: "",
      quantity: "",
      blockchain: "",
    });
  };
  return (
    <>
      <DragZone onDrop={(file) => setNft({ ...nft, file })} />
      <Box sx={{ width: "100%", ml: 2 }}>
        <Stack direction="row" spacing={2}>
          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            <InputData
              value={nft.name}
              onChange={(e) => setNft({ ...nft, name: e.target.value })}
              label={"Name"}
            />
            <InputData
              value={nft.externalLink}
              onChange={(e) => setNft({ ...nft, externalLink: e.target.value })}
              label={"externalLink"}
            />

            <InputData
              value={nft.quantity}
              onChange={(e) => setNft({ ...nft, quantity: e.target.value })}
              label={"Quantity"}
            />
          </Grid>

          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            {/* <Grid xs={14} md={9}>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  maxWidth: "max",
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <Typography>select blockchain</Typography>
                <FormControl sx={{ m: 1, minWidth: 120 }}>
                  <InputLabel>blockchain</InputLabel>
                  <Select
                    value={nft.blockchain}
                    onChange={(e) =>
                      setNft({ ...nft, blockchain: e.target.value })
                    }
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>blockchain1</MenuItem>
                    <MenuItem value={2}>blockchain2</MenuItem>
                    <MenuItem value={3}>blockchain3</MenuItem>
                  </Select>
                </FormControl>
              </Box>
            </Grid> */}
            {/* <Grid xs={14} md={9}>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <Typography>select tag</Typography>
                <FormControl sx={{ m: 1, minWidth: 120 }}>
                  <InputLabel>Tag</InputLabel>
                  <Select
                    value={nft.tag}
                    label="Tag"
                    onChange={(e) => setNft({ ...nft, tag: e.target.value })}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>Art</MenuItem>
                    <MenuItem value={2}>Music</MenuItem>
                    <MenuItem value={3}>Photo</MenuItem>
                  </Select>
                </FormControl>
              </Box>
            </Grid> */}
            <SelectBlock
            value={nft.tag}
            onChange={(e) => setNft({ ...nft, tag: e.target.value })}
            label={Tag}
            />
            <SelectBlock
            value={nft.blockchain}
            onChange={(e) => setNft({ ...nft, blockchain: e.target.value })}
            label={Blockchain}
            />
          </Grid>
        </Stack>
      </Box>

      <InputData
        value={nft.description}
        onChange={(e) => setNft({ ...nft, description: e.target.value })}
        label={"Description"}
      />

      <CreateButton addNft={addNft} />
    </>
  );
};
