import * as React from "react";
import {
  Box,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  TextField,
  Typography,
  Button,
  Stack,
  Grid,
} from "@mui/material";
import { useState, useEffect } from "react";
import Dropzone from "react-dropzone";
import md5 from "md5";
import { Theme } from "../others/Theme";
import folder from "../others/folder.png";

export const CreateProduct = () => {
  const [name, setName] = useState();
  const [file, setFile] = useState();
  const [blockchain, setBlockchain] = useState();
  const [tag, setTag] = useState();
  const [externalLink, setExternalLink] = useState();
  const [quantity, setQuantity] = useState();
  const [ItemObject, setItemObject] = useState();
  const [description, setDescription] = useState();
  const [isInvalid,setIsInvalid] = useState(false);

  // const addItem = (event) => {
  //   if(!isInvalid){
  //     return 
  //   }
  //   event.preventDefault();
  //   setItemObject({
  //     name,
  //     id: md5(name),
  //     file,
  //     blockchain,
  //     tag,
  //     externalLink,
  //     quantity,
  //     description,
  //   });
  // };
  console.log({isInvalid})
  console.log({blockchain}) 

  const addItem = useCallback(
    (event) => {
      if(!isInvalid){
            return 
          }
          event.preventDefault();
            setItemObject({
              name,
              id: md5(name),
              file,
              blockchain,
              tag,
              externalLink,
              quantity,
              description,
            });
    },
    [count]
  );


  useEffect(() => {
   if(blockchain && name && tag){
    setIsInvalid(true)
    return
   }
   setIsInvalid(false)
  }, [addItem,blockchain, name, tag]);

  return (
    <>
      <Dropzone onDrop={(file) => setFile(file)}>
        {({ getRootProps, getInputProps }) => (
          <Box
            sx={{
              bgcolor: Theme.palette.otherColor.main,
              boxShadow: 1,
              borderRadius: 5,
              p: 2,
              m: 1,
              mt: 3,
              ml: 2,
              mr: 2,
              mb: 2,
              pb: 8,
            }}
            {...getRootProps()}
          >
            <section style={{ height: "10rem" }}>
              <div>
                <input {...getInputProps()} />
                <Typography align="center">
                  <img src={folder} height={50} width={50} />
                </Typography>
                <Typography align="center">
                  Drag 'n' drop some files here, or click to select files
                </Typography>
              </div>
            </section>
          </Box>
        )}
      </Dropzone>
      <Box sx={{ width: "100%", ml: 2 }}>
        <Stack direction="row" spacing={2}>
          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            <Grid xs>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  m: 3,
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <TextField
                  label="Name"
                  error={isInvalid && !name && "Name is empty"}
                  helperText={isInvalid && !name && "Please"}
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </Box>
            </Grid>
            <Grid xs>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  m: 3,
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <TextField
                  label="External Link"
                  value={externalLink}
                  onChange={(e) => setExternalLink(e.target.value)}
                />
              </Box>
            </Grid>
            <Grid>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  m: 3,
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <TextField
                  label="Quantity"
                  value={quantity}
                  defaultValue="1"
                  type="number"
                  onChange={(e) => setQuantity(e.target.value)}
                />
              </Box>
            </Grid>
          </Grid>

          <Grid container spacing={3} sx={{ maxWidth: "50%" }}>
            <Grid xs={14} md={9}>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  maxWidth: "max",
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <Typography>select blockchain</Typography>
                <FormControl sx={{ m: 1, minWidth: 120 }}>
                  <InputLabel>blockchain</InputLabel>
                  <Select
                    value={blockchain}
                    onChange={(e) => setBlockchain(e.target.value)}
                    error={isInvalid && !blockchain  && "Blockchain is empty"}
                  helperText={isInvalid && !blockchain && "choose a blockchain"}
                  > 
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>blockchain1</MenuItem>
                    <MenuItem value={2}>blockchain2</MenuItem>
                    <MenuItem value={3}>blockchain3</MenuItem>
                  </Select>
                </FormControl>
              </Box>
            </Grid>
            <Grid xs={14} md={9}>
              <Box
                sx={{
                  bgcolor: Theme.palette.secondary.light,
                  boxShadow: 1,
                  borderRadius: 5,
                  p: 2,
                  "&:hover": {
                    backgroundColor: Theme.palette.secondary.main,
                  },
                }}
              >
                <Typography>select tag</Typography>
                <FormControl sx={{ m: 1, minWidth: 120 }}>
                  <InputLabel>Tag</InputLabel>
                  <Select
                    value={tag}
                    label="Tag"
                    onChange={(e) => setTag(e.target.value)}
                    error={isInvalid && (!tag && "tag is empty")}
                  helperText={isInvalid && !(tag && "choose a tag")}
                  >
                    <MenuItem value="">
                      <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>Art</MenuItem>
                    <MenuItem value={2}>Music</MenuItem>
                    <MenuItem value={3}>Photo</MenuItem>
                  </Select>
                </FormControl>
              </Box>
            </Grid>
          </Grid>
        </Stack>
      </Box>
      <Box
        sx={{
          bgcolor: Theme.palette.secondary.light,
          boxShadow: 1,
          borderRadius: 5,
          p: 3,
          m: 3,
          "&:hover": {
            backgroundColor: Theme.palette.secondary.main,
          },
        }}
      >
        <TextField
          label="Description"
          fullWidth
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </Box>
      <Box>
        <Button
          sx={{
            backgroundColor: Theme.palette.primary.light,
            color: "#888",
            "&:hover": {
              backgroundColor: Theme.palette.primary.main,
            },
          }}
          variant="contained"
          onClick={()=>addItem()}
        >
          create
        </Button>
      </Box>
    </>
  );
};
