import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Collapse,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import { Grid } from "@mui/material";
import { ExpandMore } from "@material-ui/icons";
import { useState } from "react";
import file1 from "../../others/file1.png";
import axios from "axios";



export const Items = ({ id, name, description, tag, quantity, blockchain }) => {
  const [expanded, setExpanded] = useState(false);
  const [item, setItem] = useState(false)

  const addToCart = (event) => {
    setItem(true)
    event.preventDefault();

    axios.post('http://localhost:3001/api/items/cart',)
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
setItem(false)
  }


  return (
    <Grid xs display="flex" justifyContent="center" alignItems="center">
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          p: 1,
          m: 1,
          bgcolor: "background.paper",
          maxWidth: 600,
          borderRadius: 1,
        }}
      >
        <Card style={{ maxWidth: 200 }}>
          <CardContent>
            <Typography variant="h5">{name}</Typography>
          </CardContent>
          <CardMedia image={file1} component="img" />
          <ExpandMore onClick={() => setExpanded(!expanded)} />
          <Collapse in={expanded}>
            <CardContent>
              <Typography variant="h6">description:{description}</Typography>
              <Typography variant="h6">tag:{tag}</Typography>
              <Typography variant="h6">quantity:{quantity}</Typography>
              <Typography variant="h6">blockchain:{blockchain}</Typography>
              <Button
                variant="contained"
                target="_blank"
                onClick={(event) => addToCart(event)}

              >
                Add to card
              </Button>
            </CardContent>
          </Collapse>
        </Card>
      </Box>
    </Grid>
  );
};
