import { Grid, Typography } from "@mui/material";
import axios from "axios";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export const ItemPage = () => {
  const params = useParams();
  const [item, setItem] = useState();
  console.log(params)

  useEffect(() => {
    axios
      .get(`http://localhost:3001/api/items/${params.id}`)
      .then((res) => {
        console.log(res);
        setItem(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [params.id]);
  if(item?.name)
  return (
    <>
      <Grid container spacing={3} justifyContent="center">
        <Grid item xs={11}>
          <Typography align="Center" variant="h5">
            {item.name}
          </Typography>
        </Grid>
        <Grid item xs={11}>
          <Typography variant="h6">description:{item.description}</Typography>
          <Typography variant="h6">tag:{item.tag}</Typography>
          <Typography variant="h6">quantity:{item.quantity}</Typography>
          <Typography variant="h6">blockchain:{item.blockchain}</Typography>
        </Grid>
      </Grid>
    </>
  );
};
